import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../../service/auth.service';
import { Alert } from 'src/app/interface/alert';
import { ActivatedRoute, Router } from '@angular/router';
import { Title } from '@angular/platform-browser';

@Component({
	selector: 'app-sign-in',
	templateUrl: './sign-in.component.html',
	styleUrls: ['./sign-in.component.scss'],
})
export class SignInComponent implements OnInit {
	signInForm!: FormGroup;
	signUpForm!: FormGroup;
	alert: Alert = {} as Alert;
	tabs: any = {
		login: true,
		signUp: false,
	};

	constructor(
		private authService: AuthService,
		private fb: FormBuilder,
		private route: Router,
		private activatedRoute: ActivatedRoute,
		private title: Title
	) {}

	ngOnInit(): void {
		this.title.setTitle('Purilo | Login');
		this.signInForm = this.fb.group({
			email: [
				'',
				[
					Validators.required,
					Validators.pattern('^[\\w-\\.]+@([\\w-]+\\.)+[\\w-]{2,4}$'),
				],
			],
			password: ['', Validators.required],
		});

		this.signUpForm = this.fb.group({
			firstName: ['', Validators.required],
			lastName: ['', Validators.required],
			email: [
				'',
				[
					Validators.required,
					Validators.pattern('^[\\w-\\.]+@([\\w-]+\\.)+[\\w-]{2,4}$'),
				],
			],
			password: ['', Validators.required],
		});
	}

	login() {
		if (this.signInForm.invalid) {
			this.signInForm.markAllAsTouched();
		} else {
			this.authService
				.login(this.signInForm.value)
				.subscribe((response) => {
					if (response) {
						this.authService.setLogin(response);
						this.route.navigate([this.getRouteToNavigate()], {
							relativeTo: this.activatedRoute,
						});
					} else {
						this.alert = {
							isSuccessMessage: false,
							isErrorMessage: true,
							message:
								'We are unable to find the account associated with this email. ' +
								'Kindly verify your credentials',
						};
					}
				});
		}
	}

	checkIfEmailExist() {
		this.authService.login(this.signUpForm.value).subscribe((response) => {
			if (response) {
				this.alert = {
					isErrorMessage: true,
					message: 'Email Id already exists.',
				};
			} else {
				this.authService
					.signUp(this.signUpForm.value)
					.subscribe((response) => {
						console.log('Sign up successful');
						//redirect to home page or to previous page
						this.authService.setLogin(response);
						this.route.navigate([this.getRouteToNavigate()], {
							relativeTo: this.activatedRoute,
						});
					});
			}
		});
	}

	signUp() {
		if (this.signUpForm.invalid) {
			this.signUpForm.markAllAsTouched();
			return;
		}

		this.checkIfEmailExist();
	}

	activateTab($event: MouseEvent, id: string) {
		$event.preventDefault();
		this.alert = {
			isErrorMessage: false,
			isSuccessMessage: false,
		};

		const keys = Object.keys(this.tabs);
		keys.map((key) => {
			this.tabs[key] = key.toString() === id;
		});
	}

	getRouteToNavigate() {
		return this.authService.currentRoute
			? this.authService.currentRoute
			: 'home';
	}
}
<div class="header">
	<nav>
		<div class="logo">
			<img alt="" src="assets/img/e-market/logo.png" />
		</div>
	</nav>
</div>
<div class="login-page">
	<div class="tab-group">
		<ul>
			<li>
				<a
					(click)="activateTab($event, 'login')"
					[class.active]="tabs.login"
					href="#"
				>
					Login
				</a>
			</li>
			<li>
				<a
					(click)="activateTab($event, 'signUp')"
					[class.active]="tabs.signUp"
					href="#"
					>Sign Up
				</a>
			</li>
		</ul>
	</div>

	<div class="tab-content-container">
		<app-alert [alertDetails]="alert"></app-alert>
		<div *ngIf="tabs.login" class="tab-content login-tab-content">
			<form (ngSubmit)="login()" [formGroup]="signInForm">
				<app-input
					[form]="signInForm"
					fieldName="email"
					formControlName="email"
					id="email"
					label="Email"
				></app-input>
				<app-input
					[form]="signInForm"
					fieldName="password"
					formControlName="password"
					id="password"
					label="Password"
					type="password"
				></app-input>
				<button class="btn btn-primary">Login In</button>
			</form>
		</div>

		<div *ngIf="tabs.signUp" class="tab-content">
			<form (ngSubmit)="signUp()" [formGroup]="signUpForm">
				<app-input
					[form]="signUpForm"
					fieldName="firstName"
					formControlName="firstName"
					id="firstName"
					label="First Name"
				></app-input>
				<app-input
					[form]="signUpForm"
					fieldName="lastName"
					formControlName="lastName"
					id="lastName"
					label="Last Name"
				></app-input>
				<app-input
					[form]="signUpForm"
					fieldName="email"
					formControlName="email"
					id="emailId"
					label="Email"
				></app-input>
				<app-input
					[form]="signUpForm"
					fieldName="password"
					formControlName="password"
					id="password"
					label="Password"
					type="password"
				></app-input>
				<button class="btn btn-primary">Sign Up</button>
			</form>
		</div>
	</div>
</div>
