import { Component, OnInit } from '@angular/core';
import { AuthService } from '../../service/auth.service';
import { MarketService } from '../../service/market.service';
import { Order } from '../../interface/order';

@Component({
	selector: 'app-profile',
	templateUrl: './profile.component.html',
	styleUrls: ['./profile.component.scss'],
})
export class ProfileComponent implements OnInit {
	isLoggedIn: boolean = false;
	orders: Order[] = [];

	constructor(
		private authService: AuthService,
		private marketService: MarketService
	) {}

	ngOnInit(): void {
		this.isLoggedIn = this.authService.isLoggedIn();
		this.getOrders();
	}

	getOrders() {
		const email = this.authService.getLoggedInUser().email;
		this.marketService.getOrders(email).subscribe((response: any) => {
			if (response.length > 0) {
				this.orders = response;
			}
		});
	}
}
<div class="page-container">
	<div class="container">
		<h1 class="page-heading">Orders</h1>
		@if (isLoggedIn) { @if(orders) {
		<div class="orders-wrapper">
			@for (order of orders; track $index) {}
			<div *ngFor="let order of orders; let i = index" class="orders">
				<h2>Order #{{ $index + 1 }}</h2>
				<ul
					*ngFor="let product of order.products"
					class="product-summary"
				>
					<li>{{ product.name }}</li>
					<li>{{ product.noOfItems + " X " + product.cost }}</li>
					<li>
						{{
							(product.noOfItems ?? 0) * product.cost
								| currency : "INR"
						}}
					</li>
				</ul>
			</div>
		</div>
		} @else {
		<h3>Shop with us and make your first order</h3>
		<div class="btn-container">
			<button class="btn btn-primary" routerLink="../home">
				Shop now
			</button>
		</div>
		} }
	</div>
</div>
