import { Component, OnInit } from '@angular/core';
import { MarketService } from '../../service/market.service';
import { Product } from '../../interface/product';
import { Title } from '@angular/platform-browser';

@Component({
	selector: 'app-cart',
	templateUrl: './cart.component.html',
	styleUrls: ['./cart.component.scss'],
})
export class CartComponent implements OnInit {
	products: Product[] = [];

	constructor(private service: MarketService, private title: Title) {}

	ngOnInit(): void {
		this.title.setTitle('Purilo | Cart');
		this.products = JSON.parse(this.service.getProducts());
		this.service.setpageName('cart');
	}

	removeItem($event: MouseEvent, productDetails: Product) {
		$event.preventDefault();
		this.products = this.products.filter((product) => {
			return product.id != productDetails.id;
		});

		this.service.setProducts(this.products);
	}

	hasProducts() {
		return this.products?.length > 0;
	}
}
<div class="page-container">
	<div class="container">
		<h1 class="page-heading">Cart</h1>
		<!-- <div class="breadcrumb">
			<ul>
				<li>
					<a routerLink="home" href="#">Home</a>
				</li>
				<li>Cart</li>
			</ul>
		</div> -->

		<div *ngIf="hasProducts()" class="cart-container">
			<table>
				<thead>
					<tr>
						<th>Item</th>
						<th>Price/Unit</th>
						<th>Quantity</th>
						<th></th>
					</tr>
				</thead>
				<tbody>
					<tr *ngFor="let product of products">
						<td>
							<span class="image-wrapper">
								<img [src]="product.imageUrl" alt="" />
							</span>
							{{ product.name }}
						</td>
						<td>{{ product.cost }} / {{ product.unit }}</td>
						<td>{{ product.noOfItems }} {{ product.unit }}</td>
						<td>
							<a (click)="removeItem($event, product)" href="#">
								<img
									src="../../../assets/svg/trash.svg"
									alt="remove"
								/>
							</a>
						</td>
					</tr>
				</tbody>
			</table>
		</div>

		<div *ngIf="!hasProducts()" class="cart-empty">
			<p>Your cart is empty</p>
			<button class="btn btn-primary" routerLink="home">
				Continue shopping
			</button>
		</div>
	</div>
</div>
