import { Component, Input, OnInit } from '@angular/core';
import { Product } from '../../interface/product';
import { MarketService } from '../../service/market.service';

@Component({
	selector: 'app-order-summary',
	templateUrl: './order-summary.component.html',
	styleUrls: ['./order-summary.component.scss'],
})
export class OrderSummaryComponent implements OnInit {
	@Input()
	products: Product[] = [];

	@Input()
	pageName: string = 'cart';
	totalCost: number = 0;
	discount: number = 0;
	isInvalidCoupon: boolean = false;
	availableCoupons: any[] = [
		{
			code: 'FRESH50',
			minValue: 200,
			discAmount: 50,
		},
		{
			code: 'FRESH100',
			minValue: 500,
			discAmount: 100,
		},
	];
	couponCode: string = '';

	constructor(private service: MarketService) {}

	ngOnInit(): void {
		this.service.pageName$.subscribe(
			(pageName) => (this.pageName = pageName)
		);
	}

	calculateSubTotal(): number {
		this.totalCost = 0;
		this.products.forEach((product) => {
			this.totalCost =
				this.totalCost + product.cost * (product.noOfItems ?? 0);
		});

		return this.totalCost;
	}

	calculateTotalCost(): number {
		this.totalCost = 0;
		this.products.forEach((product) => {
			this.totalCost =
				this.totalCost + product.cost * (product.noOfItems ?? 0);
		});

		return this.totalCost - this.discount;
	}

	validateCoupon() {
		const validCoupon = this.availableCoupons.find((coupon) => {
			return (
				coupon.code === this.couponCode.toUpperCase() &&
				this.calculateTotalCost() >= coupon.minValue
			);
		});
		if (validCoupon) {
			this.discount = validCoupon.discAmount;
			this.couponCode = '';
			this.isInvalidCoupon = false;
		} else {
			this.isInvalidCoupon = true;
		}
	}

	navigateTonextpage() {
		if (this.pageName == 'cart') {
			this.pageName = 'shipping';
			this.service.setpageName('shipping');
		} else {
			this.pageName = 'payment';
			this.service.setpageName('payment');
		}
	}
}
<div class="order-summary page-container">
	<h2>Order Summary</h2>
	<div>
		<div class="coupon-container" *ngIf="pageName == 'cart'">
			<div class="coupon-wrapper">
				<input
					type="text"
					name="coupon"
					placeholder="coupon code"
					class="coupon-input"
					[(ngModel)]="couponCode"
				/>
				<img
					class="icon-coupon"
					src="../../../../../assets/img/e-market/coupon-percent.svg"
				/>
			</div>
			<button (click)="validateCoupon()" class="btn btn-coupon">
				Apply
			</button>
		</div>
		<div *ngIf="pageName == 'cart'">
			<p class="error" *ngIf="isInvalidCoupon">Invalid coupon</p>
			<p class="helper-text">
				<strong>FRESH50</strong> save 50 on order above 200
			</p>
		</div>
		<div class="side-content">
			<span class="content-label">Subtotal</span>
			<strong>{{ calculateSubTotal() | currency : "INR" }}</strong>
		</div>
		<div class="side-content">
			<span class="content-label">Discount</span>
			<strong class="content-spl"> -{{ discount }}</strong>
		</div>
		<div class="side-content">
			<span class="content-label">Delivery</span>
			<strong>FREE</strong>
		</div>
		<div class="side-content side-content-last">
			<strong>TOTAL</strong>
			<strong>{{ calculateTotalCost() | currency : "INR" }}</strong>
		</div>
	</div>
	<button
		class="btn btn-primary"
		(click)="navigateTonextpage()"
		*ngIf="pageName == 'cart'"
	>
		Checkout
	</button>
</div>
