import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
	selector: 'app-modal',
	templateUrl: './modal.component.html',
	styleUrls: ['./modal.component.scss'],
})
export class ModalComponent implements OnInit {
	@Input() modalHeader: string = '';
	@Input() isPrimaryButtonVisible: boolean = true;
	@Input() isSecondaryButtonVisible: boolean = true;
	@Input() primaryButtonText: string = '';
	@Input() secondaryButtonText: string = '';

	@Output() submittedValue: EventEmitter<MouseEvent> = new EventEmitter();

	constructor() {}

	ngOnInit(): void {}

	openModal() {
		document.querySelector('#modal-info')?.classList.add('open');
	}

	emitValue(event: MouseEvent) {
		this.submittedValue.emit(event);
	}

	closeModal(event: MouseEvent) {
		event.preventDefault();
		document.querySelector('#modal-info')?.classList.remove('open');
	}

	applyClass() {
		return this.isPrimaryButtonVisible && !this.isSecondaryButtonVisible;
	}
}
<div class="modal-container" id="modal-info">
	<div class="modal">
		<div class="modal-header">
			<h1>{{ modalHeader }}</h1>
			<a (click)="closeModal($event)">
				<svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
					<path d="M0 0h24v24H0z" fill="none" />
					<path
						d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"
					/>
				</svg>
			</a>
		</div>
		<div class="modal-body">
			<ng-content></ng-content>
		</div>
		<div class="modal-footer">
			<div class="button-container" [class.jc-right]="applyClass()">
				<button
					class="btn btn-secondary"
					*ngIf="isSecondaryButtonVisible"
				>
					{{ secondaryButtonText }}
				</button>
				<button
					class="btn btn-primary"
					*ngIf="isPrimaryButtonVisible"
					(click)="emitValue($event)"
				>
					{{ primaryButtonText }}
				</button>
			</div>
		</div>
	</div>
</div>
