import { Component, DoCheck, Input, OnInit } from '@angular/core';
import { Product } from '../../interface/product';
import { MarketService } from '../../service/market.service';

@Component({
	selector: 'app-product-preview',
	templateUrl: './product-preview.component.html',
	styleUrls: ['./product-preview.component.scss'],
})
export class ProductPreviewComponent implements OnInit, DoCheck {
	@Input()
	productDetails: Product = {} as Product;

	constructor(private service: MarketService) {}

	ngOnInit(): void {}

	changeQuantity(event: MouseEvent, type: string) {
		event.preventDefault();
		let quantity = this.productDetails.quantity ?? 0;

		if (type === 'minus') {
			quantity -= 1;
			this.productDetails.quantity =
				quantity <= 0 ? 1 : this.productDetails.quantity;
		} else {
			this.productDetails.quantity = quantity + 1;
		}
	}

	addToCart($event: MouseEvent) {
		$event.stopPropagation();
		this.service.addToCart(this.productDetails);
	}

	//Called when a change is detected on the component
	ngDoCheck(): void {}
}
<div *ngIf="productDetails" class="product-details-container">
	<div class="product-image-wrapper">
		<img [src]="productDetails.imageUrl" alt="" />
	</div>
	<div class="product-details-wrapper">
		<div class="product-details">
			<span>Name</span>
			<span>{{ productDetails.name }}</span>
		</div>
		<div class="product-details">
			<span>Cost</span>
			<span>{{ productDetails.cost }} / {{ productDetails.unit }}</span>
		</div>

		<div class="product-details">
			<span
				*ngIf="productDetails.available"
				[ngClass]="productDetails.available ? 'available' : null"
				>In Stock</span
			>
			<span
				*ngIf="!productDetails.available"
				[ngClass]="!productDetails.available ? 'not-available' : null"
				>Out of Stock</span
			>
		</div>

		<div class="product-details product-fact">
			<p>{{ productDetails.fact }}</p>
		</div>

		<div class="quantity-container">
			<div class="quantity-wrapper">
				<a (click)="changeQuantity($event, 'minus')" href="#">
					<app-icon name="minus"></app-icon>
				</a>
				<input
					[(ngModel)]="productDetails.quantity"
					aria-label="Quantity"
					id="quantity"
					name="quantity"
					type="text"
				/>
				<a (click)="changeQuantity($event, 'plus')" href="#">
					<app-icon [name]="'add'"></app-icon>
				</a>
			</div>

			<button (click)="addToCart($event)" class="btn btn-primary">
				<app-icon name="cart"></app-icon>
			</button>
		</div>
	</div>
</div>
