import { Component, Input } from '@angular/core';

@Component({
	selector: 'app-icon',
	templateUrl: './icon.component.html',
})
export class IconComponent {
	@Input() name!: string;
}
<svg [attr.name]="name">
	<use [attr.xlink:href]="'assets/icons.svg#' + name"></use>
</svg>
