import { Component, Input } from '@angular/core';

@Component({
	selector: 'app-icon',
	templateUrl: './icon.component.html',
})
export class IconComponent {
	@Input() name!: string;
	version: number = 0;

	constructor() {
		this.version = Date.now();
	}
}
<svg [attr.id]="name">
	<use
		[attr.xlink:href]="'assets/icons.svg?v=' + version + '#icon-' + name"
	></use>
</svg>
